 public void serviceCategoriaUlContent(HttpServletRequest request, HttpServletResponse response) throws IOException, ServletException
 {
	 utf8RequestService(request);
	 String[] methodos= new String[] {
			 CategoriaHelper.ID_GET_METHOD_NAME,
			 CategoriaHelper.NOMBRE_GET_METHOD_NAME+"CategoriaParsed"
			 };

	 String param =  WebUtils.getStringParam(request, "value");
	 if(param==null || param.length() <= 0)
		 param =  WebUtils.getStringParam(request, new TrabajadorHelper().getCategoriaIdName());
	 CategoriaGroupBean mgb = CategoriaManager.getCategorias(CategoriaManager.buildSearchCriteria(param));
	 simpleResponseService(response, AjaxUtils.buildAjaxULContentFromListObj(mgb.getAlmacen(),methodos, CategoriaHelper.COMPLEX_ID_GET_METHOD,"Categoria"));
 }
