			 o.setId(WebUtils.getintParam(req, getIdName(o)));
		
			 o.setKm(WebUtils.getintParam(req, getKmName(o)));
		
			 o.setDeposito(WebUtils.getStringParam(req, getDepositoName(o)));
		
